var dd = [];
var d = [];

var bodySize = (ccpcm_global.catalogueID == 'A4')?9.6:7.8;
var italicSize = (ccpcm_global.catalogueID == 'A4')?11.05:9.36;
var paragraphMargins = [0, 4, 0, 4]; //[0, 4, 0, 4]


var styles = {

};

var htmlTest = "<h1>L'édito</h1><h2>Titre 2</h2><h3>Titre 3</h3><h4>Titre 4</h4><h5>Titre 5</h5><h6>Titre 6</h6><p><strong>p strong</strong> text normal</p>";
if ( data.html == null )
  data.html = htmlTest;
if (data.title) {
  var color = false;
  if (data.color != undefined && data.color != '') {
    try {
      color = JSON.parse(data.color);
    }
    catch(error) {
      console.log("JSON.parse(data.color)", data.color, error);
    }
  }
	if (color)
      dd.push({text: data.title, tocItem: true, tocMargin: ccpcm_global.tocMarginLevel1, tocStyle: ccpcm_global.tocStyleLevel1, contentTab: ccpcm_global.set_tab({title: data.title, color: color, forceDisplay:true}), absolutePosition: {x:0, y:-1000}}); 
    else 
	  dd.push({text: data.title, tocItem: true, tocMargin: ccpcm_global.tocMarginLevel1, tocStyle: ccpcm_global.tocStyleLevel1, contentTab: ccpcm_global.set_tab({title: data.title, forceDisplay:true}), absolutePosition: {x:0, y:-1000}});
}
var html = data.html.replace("\r\n", "<br/><br/>");
html = html.replace("&nbsp;", " "); // Important pour gérer mieux les espaces 
var htmlInPdfmake = htmlToPdfmake(html, {defaultStyles:{
  	p: { fontSize: bodySize, lineHeight: 1.3, characterSpacing: 0.1, margin:paragraphMargins}, //noWrap:true,
    i: { fontSize: italicSize, characterSpacing: -0.2, }, //22.5% more
    em: { fontSize: italicSize, characterSpacing: -0.2, }, //22.5% more
    b: { },
    strong: { },
  	h1: {fontSize:32, bold:true, marginBottom:15, characterSpacing: 0.35, color:[0,0,0,30]},
    h2: {fontSize:bodySize*2.2, bold:true, marginBottom:10, color:[0,0,0,90]},
    h3: {fontSize:bodySize*1.5, bold:true, marginBottom:7, color:ccpcm_global.editionColor, characterSpacing: 0.35,},
    h4: {fontSize:bodySize*1.2, bold:true, marginBottom:5, color:ccpcm_global.editionColor},
    h5: {fontSize:bodySize*1.1, bold:true, marginBottom:5},
    h6: {fontSize:bodySize, bold:false, marginBottom:3, color:ccpcm_global.editionColor},
    a: {color:ccpcm_global.editionColor, decoration:'underline'},
    table: {layout: 'noBorders'},
    sup: {fontSize:(bodySize/1.3), bold:true},
    sub: {fontSize:(bodySize/1.3), bold:true},
}});
console.log(htmlInPdfmake);
//console.log(htmlInPdfmake);
