// Wrap
//dd.push({columns:[{stack: [ d ], margin: ccpcm_global.insideMargins}]});
//dd.push({'columns': [ {text:'', width: ccpcm_global.insideMargins[0] - 2 + 40}, htmlInPdfmake ], columnGap: 20});
dd.push({stack: htmlInPdfmake, margin: ccpcm_global.insideMargins}); // Méthode JUSTIN pour margins
