content:
// Redefine pageSize 
ccpcm_global.pageSize = { width: 595.28, height: 793.7066666676 }; //21x28: [595.28, 793.7066666676]
ccpcm_global.catalogueID = 'A4';
ccpcm_global.sectionsHaveDescription = false; // #41


//ccpcm_global.renderDpi = 72; // Sinon 9
console.log('ccpcm_global.renderDpi', ccpcm_global.renderDpi);
// Marges 
var grandFond = 60; // extérieurs
var petitFond = 40; // Intérieurs
var deTete = 40;
var dePied = 40;

var styles = {
	/*curPage: {
     	font:'TtwellingtonsBlack', 
      	fontSize:7,
      	bold:true,
      	margin:[0,3,0,0],
    },*/
};

var foliotageOddGap = 132;
var foliotageEvenGap = 25; // #41
var foliotagePadding = 28; // #41
var foliotageFromBottom = 30;

var dd = {
  	// Foliotage
	footer: function(currentPage, pageCount, pageSize) {
      tab = ccpcm_global.get_tab(dd.content, currentPage);
      tab = tab[0];
      if (tab.hidden)
        return [];
      return [
        { 
              table: {
                  body: [
                    [{ text: currentPage, style:'curPage'},
//                      { svg: ccpcm_global.ico_foliotage,width: 100,margin:[0,3.5,0,0]}
                    ],
                    ]
              },
              layout: 'noBorders',
              absolutePosition: (currentPage % 2) ? { 
                x: (pageSize.width-foliotageOddGap-(ccpcm_global.tabWidth+foliotagePadding)), 
                y: pageSize.height - foliotageFromBottom 
              } : { 
                  x: ccpcm_global.tabWidth+foliotagePadding+foliotageEvenGap, 
                  y: pageSize.height - foliotageFromBottom
              },
          },        
      ]
   },

	// Header 
    header: function(currentPage, pageCount, pageSize) {
      	return [ ];
    },

	//AFRIQUE caraibes d\'hier et d\'Aujourdh'ui

    /*content: [
      {text: 'test', style: 'sectionTitle', color: 'white', tocItem: true, tocMargin: ccpcm_global.tocMarginLevel1, tocStyle: ccpcm_global.tocStyleLevel1, contentTab: ccpcm_global.set_tab({title: 'Jury', subtitle: 'Présidente', color: data.color_print})},
display_content()
    ],*/
    content: display_content(),
  	//content: data_test,
  	// Styles 
    styles: display_style('_Styles'),
    // DefaultStyles
    defaultStyle: {
      font: 'Ttwellingtons',
      color: ccpcm_global.black,
    },
    // Page setup 
    info: {
        title: 'Catalogue 2019 du Festival International du Film d\'Amiens',
        author: 'Direction de la publication : Équipe du FIFAM - Direction artistique : Annouchaka De Andrade - Conception : Justin Petermann & Wilhem Arnoldy',
        subject: 'Le catalogue présente l\'ensemble de la programmation de l\'édition 39',
        keywords: 'film,festival,catalogue',
    },
    compress: false,
    pageSize: ccpcm_global.pageSize, 
    pageOrientation: 'portrait',
//  pageMargins:[0,0,0,dePied],
    pageMargins : function(currentPage) {
      var margins = {};
      if ((currentPage + 1) % 2) { // impair
        margins = {left:ccpcm_global.tabWidth,top:40, right:0,bottom:40};
      } else { // pair
        margins = {left:0,top:40, right:ccpcm_global.tabWidth,bottom:40};
      }
      console.log(currentPage, margins);
      return margins;
    },
    pageBreakBefore: function(currentNode, followingNodesOnPage, nodesOnNextPage, previousNodesOnPage) {
     return currentNode.headlineLevel === 1 && followingNodesOnPage.length === 0;
  } // https://pdfmake.github.io/docs/document-definition-object/page/#dynamically-control-page-breaks-for-instance-to-avoid-orphan-children
}


dd.header = function(currentPage, pageCount, pageSize) {
  tab = ccpcm_global.get_tab(dd.content, currentPage);
  tab = tab[0];
  if (tab.hidden)
    return [];
  return [
    {
      canvas: [
        {
          type: 'rect',
          x: ((currentPage + 1)  % 2) ? (-ccpcm_global.cropMarksBleedOff) : pageSize.width - ccpcm_global.tabWidth,
          y: 0,
          w: ccpcm_global.tabWidth, 
          h: pageSize.height,
          color: tab.color, // Couleur CMJN
        },
      ]
    },
    { 
      table: {
        widths: [ ccpcm_global.tabWidth-10 ],
        body: ccpcm_global.rotatedTextTableBody(tab),
      },
      layout: "noBorders",
      absolutePosition: { x:((currentPage + 1) % 2) ? 2 : pageSize.width - ccpcm_global.tabWidth-2, y:90 }
    }
  ]
};

dd.header = function(currentPage, pageCount, pageSize) {
  tab = ccpcm_global.get_tab(dd.content, currentPage);
  return ccpcm_global.get_tab_display(currentPage, pageSize, tab);
};

ccpcm_global.master = dd;
