// Redefine pageSize 
ccpcm_global.pageSize = { width: 595.28, height: 793.7066666676 }; //21x28: [595.28, 793.7066666676]
ccpcm_global.catalogueID = 'A4';
ccpcm_global.sectionsHaveDescription = false; // #41


//ccpcm_global.renderDpi = 300; // Sinon 9
console.log('ccpcm_global.renderDpi', ccpcm_global.renderDpi);
ccpcm_global.black = [0,0,0,100]; // PRINT 

//2,834646761984861 = 1mm tiré de 4A0 calcul
//1pt = 0,352778 mm
//1pdf unite = 1pt
ccpcm_global.cropMarksLength = 28.3464676198; // 10mm : 28.3464676198

ccpcm_global.pageSize= {
	width: ccpcm_global.pageSize.width + ccpcm_global.cropMarksLength * 2,
  	height: ccpcm_global.pageSize.height + ccpcm_global.cropMarksLength * 2,
}

// 2mm : 5.669333333333334 / 3mm : 8.504 / 5mm : 14.1732338099
ccpcm_global.cropMarksSpace = 14.1732338099; //5mm
ccpcm_global.cropMarksOverlay  = 5.669333333333334; //2mm

ccpcm_global.cropMarksLineWidth = 0.3; //.25
ccpcm_global.cropMarksRectWidth = 2; // 20
ccpcm_global.cropMarksMarksLength = ccpcm_global.cropMarksLength - ccpcm_global.cropMarksSpace;

ccpcm_global.cropMarksBleedOff = 14.1732338099; // 5mm : 14.1732338099

ccpcm_global.cropMarksDrawHorizontal = function(x_position, y_position, bg = true, appendHeightLength = 0) {
  var color = false;
  var lineWidth = false;
  if (bg) {
    color = [0,0,0,100]; // Trait de coupe
    lineWidth = ccpcm_global.cropMarksLineWidth;
  } else {
    color = [0,0,0,0]; // fond blanc
    lineWidth = ccpcm_global.cropMarksRectWidth;
  }
  var x = 0;
  var y = 0;
  if (x_position == 'left') {
  } else {
     x += ccpcm_global.pageSize.width - ccpcm_global.cropMarksLength + ccpcm_global.cropMarksSpace - ccpcm_global.cropMarksOverlay;
  }
  if (y_position == 'bottom') {
    y -= lineWidth / 2 + ccpcm_global.cropMarksSpace;
  } else {
    y -= ccpcm_global.pageSize.height - ccpcm_global.cropMarksLength * 2 + lineWidth / 2 + ccpcm_global.cropMarksSpace;
  }
  y += appendHeightLength;
  return {
    type: 'rect',
    x: x,
    y: y,
    w: ccpcm_global.cropMarksMarksLength + ccpcm_global.cropMarksOverlay,
    h: lineWidth,
    color: color, // Couleur CMJN
  }
}

ccpcm_global.cropMarksDrawVertical = function(x_position, y_position, bg = true, appendHeightLength = 0) {
  var color = false;
  var lineWidth = false;
  if (bg) {
    color = [0,0,0,100];
    lineWidth = ccpcm_global.cropMarksLineWidth;
  } else {
    color = [0,0,0,0];
    lineWidth = ccpcm_global.cropMarksRectWidth;
  }
  var x = 0;
  var y = 0;
  if (x_position == 'left') {
    x += ccpcm_global.cropMarksLength - lineWidth / 2;
  } else {
    x += ccpcm_global.pageSize.width - ccpcm_global.cropMarksLength - lineWidth / 2;
  }
  if (y_position == 'bottom') {
    y += ccpcm_global.cropMarksSpace - ccpcm_global.cropMarksSpace - ccpcm_global.cropMarksOverlay;
  } else {
    y -= ccpcm_global.pageSize.height - ccpcm_global.cropMarksLength + ccpcm_global.cropMarksSpace;
  }
  y += appendHeightLength;
  return {
    type: 'rect',
    x: x,
    y: y,
    w: lineWidth ,
    h: ccpcm_global.cropMarksMarksLength + ccpcm_global.cropMarksOverlay,
    color: color, // Couleur CMJN
  }
}
var borderLineWidth = 0.25;
var borderLineDisplay = false;
var marginsDisplay = false;
ccpcm_global.cropMarksDisplay = function(appendHeight = false) {
	var canvas = [];
  	var appendHeightLength = 0;
  	if (appendHeight)
      appendHeightLength =  - ccpcm_global.cropMarksLength + ccpcm_global.pageSize.height + ccpcm_global.cropMarksSpace;
  	//console.log (appendHeightLength)

  	if (borderLineDisplay) {
      canvas.push({
        type: 'rect',
        x: 0,
        y: -borderLineWidth - ccpcm_global.cropMarksSpace + appendHeightLength,
        w: ccpcm_global.pageSize.width,
        h: borderLineWidth, 
        color: [0,0,0,20], // Couleur CMJN
	  });
      canvas.push({
        type: 'rect',
        x: 0,
        y: - ccpcm_global.pageSize.height + ccpcm_global.cropMarksLength * 2 - ccpcm_global.cropMarksSpace + appendHeightLength, 
        w: ccpcm_global.pageSize.width,
        h: borderLineWidth,
        color: [0,0,0,20], // Couleur CMJN
	  });
      canvas.push({
        type: 'rect',
        x: ccpcm_global.cropMarksLength,
        y: - ccpcm_global.pageSize.height + ccpcm_global.cropMarksLength  - ccpcm_global.cropMarksSpace + appendHeightLength,
        w: borderLineWidth, 
        h: ccpcm_global.pageSize.height ,
        color: [0,0,0,20], // Couleur CMJN
	  });
      canvas.push({
        type: 'rect',
        x: ccpcm_global.pageSize.width - ccpcm_global.cropMarksLength - borderLineWidth,
        y: - ccpcm_global.pageSize.height - ccpcm_global.cropMarksLength - ccpcm_global.cropMarksSpace + appendHeightLength,
        w: borderLineWidth, 
        h: ccpcm_global.pageSize.height + ccpcm_global.cropMarksLength * 2,
        color: [0,0,0,20], // Couleur CMJN
	  });
    }

    if (marginsDisplay) {
      canvas.push({
        type: 'rect',
        x: 0,
        y: -borderLineWidth - ccpcm_global.cropMarksSpace + appendHeightLength - ccpcm_global.insideMargins[3],
        w: ccpcm_global.pageSize.width,
        h: borderLineWidth, 
        color: [100,0,0,0], // Couleur CMJN
	  });
      canvas.push({
        type: 'rect',
        x: 0,
        y: - ccpcm_global.pageSize.height + ccpcm_global.cropMarksLength * 2 - ccpcm_global.cropMarksSpace + appendHeightLength + ccpcm_global.insideMargins[1], 
        w: ccpcm_global.pageSize.width,
        h: borderLineWidth,
        color: [100,0,0,0], // Couleur CMJN
	  });
      canvas.push({
        type: 'rect',
        x: ccpcm_global.cropMarksLength + ccpcm_global.insideMargins[0],
        y: - ccpcm_global.pageSize.height + ccpcm_global.cropMarksLength  - ccpcm_global.cropMarksSpace + appendHeightLength,
        w: borderLineWidth, 
        h: ccpcm_global.pageSize.height ,
        color: [100,0,0,0], // Couleur CMJN
	  });
      canvas.push({
        type: 'rect',
        x: ccpcm_global.pageSize.width - ccpcm_global.cropMarksLength - borderLineWidth - ccpcm_global.insideMargins[2],
        y: - ccpcm_global.pageSize.height - ccpcm_global.cropMarksLength - ccpcm_global.cropMarksSpace + appendHeightLength,
        w: borderLineWidth, 
        h: ccpcm_global.pageSize.height + ccpcm_global.cropMarksLength * 2,
        color: [100,0,0,0], // Couleur CMJN
	  });
    }

  	canvas.push(ccpcm_global.cropMarksDrawHorizontal('left', 'bottom', false, appendHeightLength));
    canvas.push(ccpcm_global.cropMarksDrawVertical('left', 'bottom', false, appendHeightLength));
    canvas.push(ccpcm_global.cropMarksDrawHorizontal('left', 'top', false, appendHeightLength));
    canvas.push(ccpcm_global.cropMarksDrawVertical('left', 'top', false, appendHeightLength));
	canvas.push(ccpcm_global.cropMarksDrawHorizontal('right', 'top', false, appendHeightLength));
    canvas.push(ccpcm_global.cropMarksDrawVertical('right', 'top', false, appendHeightLength));
    canvas.push(ccpcm_global.cropMarksDrawHorizontal('right', 'bottom', false, appendHeightLength));
    canvas.push(ccpcm_global.cropMarksDrawVertical('right', 'bottom', false, appendHeightLength));

    canvas.push(ccpcm_global.cropMarksDrawHorizontal('left', 'bottom', true, appendHeightLength));
    canvas.push(ccpcm_global.cropMarksDrawVertical('left', 'bottom', true, appendHeightLength));
    canvas.push(ccpcm_global.cropMarksDrawHorizontal('left', 'top', true, appendHeightLength));
    canvas.push(ccpcm_global.cropMarksDrawVertical('left', 'top', true, appendHeightLength));
	canvas.push(ccpcm_global.cropMarksDrawHorizontal('right', 'top', true, appendHeightLength));
    canvas.push(ccpcm_global.cropMarksDrawVertical('right', 'top', true, appendHeightLength));
    canvas.push(ccpcm_global.cropMarksDrawHorizontal('right', 'bottom', true, appendHeightLength));
    canvas.push(ccpcm_global.cropMarksDrawVertical('right', 'bottom', true, appendHeightLength));
  return {
  	canvas: canvas
  }
}
