var grandFond = ccpcm_global.masterMargins[0]; // extérieurs
var petitFond = ccpcm_global.masterMargins[2]; // Intérieurs
var deTete = ccpcm_global.masterMargins[1];
var dePied = ccpcm_global.masterMargins[3];

var styles = {

};

var foliotageOddGap = 132;
var foliotageEvenGap = 25; // #41
var foliotagePadding = 28; // #41
var foliotageFromBottom = 30;

var dd = {
  	// Foliotage
	footer: function(currentPage, pageCount, pageSize) { 
      tab = ccpcm_global.get_tab(dd.content, currentPage);
  	  tab = tab[0];
      if (tab.hidden)
        return [];
      return [
        { 
              table: {
                  body: [
                    [{ text: currentPage, style:'curPage'},
                      { svg: ccpcm_global.ico_foliotage,width: 110,margin:[0,3.5,0,0]}],
                    ]
              },
              layout: 'noBorders',
              absolutePosition: (currentPage % 2) ? { 
                x: (pageSize.width-foliotageOddGap-(ccpcm_global.tabWidth+foliotagePadding) - ccpcm_global.cropMarksLength), 
                y: pageSize.height - foliotageFromBottom - ccpcm_global.cropMarksLength
              } : { 
                x: ccpcm_global.tabWidth+foliotagePadding+foliotageEvenGap + ccpcm_global.cropMarksLength, 
                y: pageSize.height - foliotageFromBottom - ccpcm_global.cropMarksLength
              },
          },        
      ]
   },

	// Header 
    header: function(currentPage, pageCount, pageSize) {
      	return [ ];
    },

    content: display_content(),
  	//content: data_test,
  	// Styles 
    styles: display_style('_Styles'),
    // DefaultStyles
    defaultStyle: {
      font: 'Bureau_Grot',
      color: ccpcm_global.black,
    },
    // Page setup 
    info: {
        title: 'Catalogue du Festival International du Film d\'Amiens',
        author: 'Direction de la publication : Équipe du FIFAM - Direction artistique : Annouchaka De Andrade - Conception : Justin Petermann & Wilhem Arnoldy',
        subject: 'Le catalogue présente l\'ensemble de la programmation de l\'édition 39',
        keywords: 'film,festival,catalogue',
    },
    compress: false,
    pageSize: ccpcm_global.pageSize, 
    pageOrientation: 'portrait',
//  pageMargins:[0,0,0,dePied],
    pageMargins : function(currentPage) {
      var margins = {};
      if ((currentPage + 1) % 2) { // impair
        margins = {left:ccpcm_global.tabWidth + ccpcm_global.cropMarksLength,top:40 + ccpcm_global.cropMarksLength, right:0 + ccpcm_global.cropMarksLength,bottom:40 + ccpcm_global.cropMarksLength, cropMarksLength: ccpcm_global.cropMarksLength};
      } else { // pair
        margins = {left:0+ ccpcm_global.cropMarksLength,top:40 + ccpcm_global.cropMarksLength, right:ccpcm_global.tabWidth + ccpcm_global.cropMarksLength,bottom:40+ ccpcm_global.cropMarksLength, cropMarksLength: ccpcm_global.cropMarksLength};
      }
      console.log(currentPage, margins);
      return margins;
    },
    pageBreakBefore: function(currentNode, followingNodesOnPage, nodesOnNextPage, previousNodesOnPage) {
     return currentNode.headlineLevel === 1 && followingNodesOnPage.length === 0;
  } // https://pdfmake.github.io/docs/document-definition-object/page/#dynamically-control-page-breaks-for-instance-to-avoid-orphan-children
}


dd.header = function(currentPage, pageCount, pageSize) {
  tab = ccpcm_global.get_tab(dd.content, currentPage);
  return ccpcm_global.get_tab_display(currentPage, pageSize, tab);
};

ccpcm_global.master = dd;
